import React, { Component} from 'react'
import {Nav, Navbar, NavDropdown, Form, Button,Table,Modal} from "react-bootstrap";
import axios from 'axios';
    
    export class getCourse extends Component{
    
        state = {
            subjects:[],
            count:1
        }
    
        componentDidMount() {
            axios.get('http://localhost:5000/subject/getSubjects').
            then(res => {
                const subjects = res.data.allSubject;
                console.log(subjects);
                this.setState({subjects:subjects});
            }).catch(err => err.message)
        }
    
    
        render()
        {
            return(
                <div className="container" style={{paddingTop:'50px'}}>
                    <Table striped bordered hover variant="dark">
                        <thead>
                        <tr>
                            <th>#</th>
                            <th>Subject Name</th>
                            <th>Description</th>
                            <th>Amount</th>
    
    
                        </tr>
                        </thead>
                        <tbody>
                        {this.state.subjects.map(subject => (
                            <tr>
                                <td>{this.state.count}</td>
                                <td>{subject.name}</td>
                                <td>{subject.description}</td>
                                <td>{subject.amount}</td>
                            </tr>
                        ))}
    
                        </tbody>
                    </Table>
                </div>
            )
        }
    
    }
   

export default getCourse;
